



CREATE TABLE "user" (
                        seq bigint GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                        email varchar(255) NOT NULL,
                        password varchar(255) NOT NULL,
                        role varchar(255) NOT NULL,
                        name varchar(255) NOT NULL,
                        last_login_at timestamp DEFAULT NULL,
                        created_at timestamp NOT NULL,
                        modified_at timestamp DEFAULT NULL
);



CREATE TABLE user_detail (
                             detail_seq bigint GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                             blocked boolean NOT NULL,
                             greeting varchar(255) DEFAULT NULL,
                             created_at datetime NOT NULL,
                             modified_at datetime NOT NULL,
                             last_login_at datetime DEFAULT NULL,
                             user_seq bigint DEFAULT NULL unique,
                             PRIMARY KEY (detail_seq),
                             FOREIGN KEY (user_seq) REFERENCES "user" (seq)
);

CREATE TABLE user_profile (
                              seq bigint GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  name varchar(255) NOT NULL,
  path varchar(255) NOT NULL,
  uuid varchar(255) NOT NULL,
  size bigint NOT NULL,
  extension varchar(255) NOT NULL,
  created_at datetime(6) NOT NULL,
  modified_at datetime(6) NOT NULL,
  user_seq bigint NOT NULL,
  PRIMARY KEY (seq),
  CONSTRAINT FK_USER_SEQ_IN_PROFILE FOREIGN KEY (user_seq) REFERENCES user_detail (user_seq)
);


CREATE TABLE friend_relationship (
                                     seq bigint GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                                     user_seq bigint NOT NULL,
                                     friend_seq bigint NOT NULL,
                                     status varchar(255) NOT NULL,
                                     created_at datetime NOT NULL,
                                     modified_at datetime DEFAULT NULL,
                                     PRIMARY KEY (seq),
                                     FOREIGN KEY (friend_seq) REFERENCES "user" (seq),
                                     FOREIGN KEY (user_seq) REFERENCES "user" (seq)
);

